const navMenu = document.querySelector('.nav-menu');
const navLinks = document.querySelectorAll('.nav-link');
const sectionHamburger = document.querySelector('.sidebar-hamburger');

if (hamburger && navMenu) {
    hamburger.addEventListener('click', () => {
        hamburger.classList.toggle('active');
        navMenu.classList.toggle('active');
        // Hide section hamburger when nav menu is open
        if (sectionHamburger) {
            if (navMenu.classList.contains('active')) {
                sectionHamburger.style.display = 'none';
            } else {
                sectionHamburger.style.display = '';
            }
        }
    });

    navLinks.forEach(link => {
        link.addEventListener('click', () => {
            hamburger.classList.remove('active');
            navMenu.classList.remove('active');
            // Restore section hamburger when nav closes
            if (sectionHamburger) {
                sectionHamburger.style.display = '';
            }
        });
    });
}

// ===== SMOOTH SCROLLING =====
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function (e) {
        const target = document.querySelector(this.getAttribute('href'));
        if (target) {
            e.preventDefault();
            const offsetPosition = target.getBoundingClientRect().top + window.pageYOffset - 70;
            window.scrollTo({ top: offsetPosition, behavior: 'smooth' });
        }
    });
});

// ===== SCROLL HANDLER (active nav link) =====
let scrollTimeout;
window.addEventListener('scroll', () => {
    if (scrollTimeout) return;
    scrollTimeout = setTimeout(() => {
        let current = '';
        document.querySelectorAll('section').forEach(section => {
            if (pageYOffset >= section.offsetTop - 100) {
                current = section.getAttribute('id');
            }
        });
        navLinks.forEach(link => {
            link.classList.remove('active');
            if (link.getAttribute('href') === `#${current}`) {
                link.classList.add('active');
            }
        });
        scrollTimeout = null;
    }, 100);
});

// ===== DYNAMIC YEAR IN FOOTER =====
const footerText = document.querySelector('.footer-bottom p');
if (footerText) {
    footerText.textContent = `\u00A9 ${new Date().getFullYear()} Bohlebots-Pompeii. All rights reserved. Rising from the ashes, reaching for the stars.`;
}

// ===== LOADING ANIMATION =====
window.addEventListener('load', () => {
    document.body.style.transition = 'opacity 0.4s ease';
    document.body.style.opacity = '1';
});

// ===== MOBILE NAVIGATION =====
const hamburger = document.querySelector('.hamburger');
const navMenu = document.querySelector('.nav-menu');
const navLinks = document.querySelectorAll('.nav-link');
const sectionHamburger = document.querySelector('.sidebar-hamburger');

if (hamburger && navMenu) {
    hamburger.addEventListener('click', () => {
        hamburger.classList.toggle('active');
